## Azure LZ Reporting Tool ##

# Connect to Azure
Connect-AzAccount

# Connect to Azure AD
Connect-AzureAD -AzureEnvironmentName "AzureCloud"


[void] [System.Reflection.Assembly]::LoadWithPartialName("System.Drawing")
[void] [System.Reflection.Assembly]::LoadWithPartialName("System.Windows.Forms")
[void] [System.Windows.Forms.Application]::EnableVisualStyles()


## Button Functions
# Button 1 function (Subscriptions)
function GetSubs
{
Get-AzSubscription | Sort-Object Name | Out-GridView
}

# Button 2 function (Apps)
function GetApps
{
Get-AzureADApplication | Select-Object -Property ObjectId,DisplayName | Sort-Object displayName | Out-GridView
}

# Button 3 function (Users)
function GetUsers
{
Get-AzureADUser | Sort-Object displayName | Out-GridView
}

# Button 4 function (AD Service Principles)
function GetADSPs
{
Get-AzureADServicePrincipal | Sort-Object DisplayName| Out-GridView
}

# Button 5 function (Groups)
function GetGroups
{
#$Properties = @("DisplayName","Id","SecurityEnabled","MailNickname")
Get-AzureADGroup | Sort-Object DisplayName| Out-GridView
}

# Button 6 function (Roles)
function GetRoles
{
Get-AzureAdDirectoryRole | Sort-Object displayName | Out-GridView
}


# Form
$Form                   = New-Object system.Windows.Forms.Form
$Form.Size              = New-Object System.Drawing.Size(1000,800)
$Form.BackColor         = "#ffffff"
$form.MaximizeBox       = $false
$Form.StartPosition     = "CenterScreen"
$Form.FormBorderStyle   = 'Fixed3D'
$Form.Text              = "Azure LZ Reporting Tool"
$Form.ForeColor         = "#33333"

