## Button Functions
# Button 7 function (Quotas)
function GetQuotas
{

# Import export-excel module to make pretty reports
if (Get-Module -ListAvailable -Name ImportExcel) {
    Write-Host "The ImportExcel Module exists"
} 
else {
    Install-Module -Name  -Force -Verbose
}


# Declare xls file
$path = "C:\temp\997_Azure_Core_Count.xls"

# Clean existing files
$deleteFiles = @("$path")

foreach ($deleteFiles in $deleteFiles) {

if (Test-Path $deleteFiles) {
    Remove-Item $deleteFiles
}  

}


$subs = Get-AzSubscription

# Create Report (997_Azure_Core_Count.xls) 
foreach($s in $subs) {

Select-AzSubscription -Subscription $s
$names = Get-AzVMUsage -Location "Canada Central"
$output= foreach($name in $names) {$results = [pscustomobject]@{'name'=$name.name.LocalizedValue;'value'=$name.CurrentValue;'limit'=$name.Limit};$results}
$output |Export-Excel -Path $path -WorksheetName $s.Name -AutoSize -FreezeTopRow -TableStyle Medium2 -Verbose
}

Invoke-Item $path

}

# Button 8 function (GroupMembers)
function GetGroupMembers
{

# Import export-excel module to make pretty reports
if (Get-Module -ListAvailable -Name ImportExcel) {
}


# Import AzureAD Module
if (Get-Module -ListAvailable -Name AzureAD) {
    Write-Host "The Azure AD Module exists"
} 
else {
    Install-Module -Name AzureAD -Force -Verbose
}


# Declare xls file
$path = "C:\temp\Get-AzADGroupMember(CSD-Only).xls"

# Clean existing files
$deleteFiles = @("$path")

foreach ($deleteFiles in $deleteFiles) {
    Remove-Item $deleteFiles
}


# Create Report
$groups = Get-AzADGroup | Where-Object {$_.displayName -like "*CSD*"} | Sort-Object displayName

foreach($groups in $groups){

$members = Get-AzADGroupMember -GroupDisplayName $groups.DisplayName
$members | Select-Object displayName,id

$output= foreach($member in $members) {$results = [pscustomobject]@{'displayName'=$member.displayName;'id'=$member.id};$results}
$output |Export-Excel -Path $path -WorksheetName $groups.displayName -AutoSize -FreezeTopRow -TableStyle Medium2 -Verbose

}

Invoke-Item $path

}


